function toSameOriginUrl(input) {
  if (!input) return null;
  try {
    // 相対URLも許可しつつ、同一オリジンのみOKにする
    const u = new URL(input, window.location.origin);
    return u.origin === window.location.origin ? u.href : null;
  } catch (_) {
    return null;
  }
}

const fallback = window.location.origin + '/'; // トップ
const redirectUrl = toSameOriginUrl(candidate) || fallback;

// 保存
