(() => {
  const el = document.getElementById('intro-smart');
  if (!el) return;

  const row = collections['db2c2f49-0e7a-4791-81fe-b16e21e6302a']?.data?.[0] || {};
  const fix = u => typeof u === 'string' ? u.replace(/%2F/gi, '/') : u;

  const src    = fix(row.intro_video_path || '');
  const poster = fix(row.intro_video_thumbnail || '');
  const startAt = 0.3;

  const img     = el.querySelector('.sv-thumb');
  const playBtn = el.querySelector('.sv-play-btn');

  // 1) サムネ表示
  if (img && poster) img.src = poster;

  // 2) video生成（親がheight:300pxで角丸10px）
  const v = document.createElement('video');
  v.playsInline = true;
  v.preload = 'metadata';
  v.controls = false;
  v.autoplay = false;
  if (poster) v.poster = poster;
  if (src)    v.src    = src;
  // ここでは中央配置だけしておく（幅・高さはloadedmetadataで決める）
  v.style.cssText = `
    position:absolute;
    top:50%;
    left:50%;
    transform:translate(-50%, -50%);
    background:#000;
    border-radius:10px;
  `;
  el.appendChild(v);

  // 3) 動画の向きに応じてサイズを調整（高さ300px固定）
  const applySize = () => {
    const boxH = 300; // 固定高さ
    const w = v.videoWidth;
    const h = v.videoHeight;

    if (!w || !h) {
      // 取れないときは一旦コンテナいっぱい
      v.style.width = '100%';
      v.style.height = '100%';
      v.style.objectFit = 'contain';
      return;
    }

    if (w >= h) {
      // 横長 → 高さ300、その比率で幅を計算（16:9相当）
      const ratio = w / h; // だいたい 16/9
      const targetW = boxH * ratio;
      v.style.width = targetW + 'px';
      v.style.height = boxH + 'px';
      v.style.objectFit = 'cover';   // 横長はカバー気味に
    } else {
      // 縦長 → 高さ300、幅は縦動画の比率で
      const ratio = w / h; // だいたい 9/16
      const targetW = boxH * ratio;
      v.style.width = targetW + 'px';
      v.style.height = boxH + 'px';
      v.style.objectFit = 'contain'; // 縦長は全体見せたい
    }
  };

  v.addEventListener('loadedmetadata', () => {
    // 0.3s にしたいとき
    try {
      if (startAt > 0 && v.currentTime < startAt - 0.05) {
        v.currentTime = Math.min(startAt, isFinite(v.duration) ? v.duration - 0.01 : startAt);
      }
    } catch {}
    applySize();
  }, { once:true });

  // 4) タップで再生/停止
  const togglePlay = async () => {
    if (v.paused) {
      try { await v.play(); } catch {}
    } else {
      v.pause();
    }
  };
  // コンテナ全体タップ
  el.addEventListener('click', () => {
    togglePlay();
  });

  // 5) 再生でサムネ＆再生ボタンを消す
  const hideOverlay = () => {
    if (img) {
      img.style.transition = 'opacity .25s';
      img.style.opacity = '0';
      img.style.pointerEvents = 'none';
      setTimeout(() => { try { img.remove(); } catch {} }, 300);
    }
    if (playBtn) {
      playBtn.style.transition = 'opacity .2s';
      playBtn.style.opacity = '0';
      playBtn.style.pointerEvents = 'none';
      setTimeout(() => { try { playBtn.remove(); } catch {} }, 200);
    }
  };
  v.addEventListener('play', hideOverlay, { once:true });

  // 6) 停止したらまたボタン出したい場合
  v.addEventListener('pause', () => {
    // サムネは戻さないけど、ボタンだけ戻す場合
    if (playBtn && !el.contains(playBtn)) el.appendChild(playBtn);
    if (playBtn) {
      playBtn.style.opacity = '1';
      playBtn.style.pointerEvents = 'auto';
    }
  });
})();

