if (!lastSent) return true;

// 経過ミリ秒を計算
const elapsedMs = Date.now() - new Date(lastSent).getTime();

// 60秒 = 60000ミリ秒以上経過しているか？
const isOver60sec = elapsedMs >= 60 * 1000;

// 結果を返す
return isOver60sec;

