// 事前に用意された保存候補URL（WeWebの variables から取得）variables[/* login redirect url */'698dffa2-0aca-4e15-abbf-aec77ce383f0']
const VAR_KEY = '698dffa2-0aca-4e15-abbf-aec77ce383f0';
const raw = (typeof variables !== 'undefined' && variables[VAR_KEY]) ?? '';
const candidate = typeof raw === 'string' ? raw.trim() : '';

function toSameOriginUrl(input) {
  if (!input) return null;
  try {
    // 相対URLも許可しつつ、同一オリジンのみOKにする
    const u = new URL(input, window.location.origin);
    return u.origin === window.location.origin ? u.href : null;
  } catch (_) {
    return null;
  }
}

const fallback = window.location.origin + '/'; // トップ
const redirectUrl = toSameOriginUrl(candidate) || fallback;

// 保存
localStorage.setItem('postLoginRedirect', redirectUrl);
