    typeof CLIENT_SECRET === "string" &&
    CLIENT_SECRET.startsWith("pi_") &&
    CLIENT_SECRET.includes("_secret_");

  if (!isSecretOk) {
    if (btn) btn.style.display = "none";
    return;
  }

  const stripe = Stripe(STRIPE_PUBLISHABLE_KEY);

  stripe.retrievePaymentIntent(CLIENT_SECRET).then(({ paymentIntent }) => {
    if (!paymentIntent) {
      if (btn) btn.style.display = "none";
      return;
    }

    const mountPaymentElement = () => {
      const container = document.querySelector("#payment-element");
      if (!container) {
        setTimeout(mountPaymentElement, 150);
        return;
      }

      const elements = stripe.elements({
        clientSecret: CLIENT_SECRET,
      });

      const paymentElement = elements.create("payment");
      paymentElement.mount("#payment-element");

      // confirm 用に保持（既存設計を踏襲）
      window.__reqmeStripe = stripe;
      window.__reqmeElements = elements;
    };

    if (
      paymentIntent.status === "requires_payment_method" ||
      paymentIntent.status === "requires_action"
    ) {
      if (btn) btn.style.display = "inline-block";
      mountPaymentElement();
      return;
    }

    // すでに処理済み
    const container = document.querySelector("#payment-element");
    if (container) {
      container.innerHTML =
        '<p style="font-size:14px;">このお支払いはすでに処理済みです。</p>';
    }
    if (btn) btn.style.display = "none";
  });
})();

