return (async () => {
  const projectUrl = formulas["d7f50be0-d58e-455f-9e1b-676b14439d2f"]();

  const SAVE_ENDPOINT = `https://${projectUrl}.supabase.co/functions/v1/stripe-save-payment-method`;

  // ★ PaymentIntent を作ったときに保存しておいたやつを使う
  const PAYMENT_INTENT_ID =
    variables[/* stripe intent id */ "d8803c6b-ca27-4cb7-a6d5-f0644b38461a"];

  // createが返したclient_secretもいつもの変数に入ってる想定
  const CLIENT_SECRET =
    variables[/* stripe client */ "3c355d62-ea14-4977-a7f7-c9424455b1a4"];

  // --- 入力値の取得（今まで通り） ---
  const CREATOR_COLL =
