  const REQUEST_USER_ID = USER_COLL.id || null;

  const rawType = (
    globalContext?.pageParameters?.["type"] || ""
  )
    .toString()
    .toLowerCase()
    .trim();
  const MESSAGE_TYPE = rawType === "voice" ? "voice" : "video";

  const PRICE_YEN = Number(
    MESSAGE_TYPE === "voice"
      ? CREATOR_COLL?.price_voice
      : CREATOR_COLL?.price_video
  );

  const GENRE =
    variables["aae1c256-d2cb-4183-a067-7452c1b4838c"] ?? null;
  const TO_TYPE =
    variables["4cc0890c-9daf-4bca-9404-b29d343e6965"] ?? null;
  const TO_NAME =
    variables["d40bc8f1-85fa-4680-a0dc-ef3fb748c958-value"] ?? null;
  const MSG =
    variables["c8e029e8-6e75-46c8-b9ba-f2fc7cdadf8a-value"] ?? null;
  const MEMO =
    variables["197a6984-1190-41aa-a071-4382f59c93f7-value"] ?? null;

  // Supabase JWT
  const JWT =
    pluginVariables["1fa0dd68-5069-436c-9a7d-3b54c340f1fa"]?.user?._session
      ?.access_token;

  const errBox = document.getElementById("payment-errors");
  const setErr = (m) => {
    if (errBox) errBox.textContent = m || "";
  };

  const stripe = window.__reqmeStripe;
  const elements = window.__reqmeElements;

  try {
    // 前提チェック
    if (!stripe || !elements || !CLIENT_SECRET) {
      setErr("決済フォームの初期化に失敗しました。ページを更新してください。");
      throw new Error("INIT_ERROR");
    }
    if (!JWT) {
      setErr("ログインの有効期限が切れています。再度ログインしてください。");
      throw new Error("MISSING_JWT");
    }
    if (!PAYMENT_INTENT_ID) {
      setErr("決済IDが見つかりません。最初からやり直してください。");
      throw new Error("MISSING_PI_ID");
    }
    if (
      !SELLER_CREATOR_ID ||
      !REQUEST_USER_ID ||
      !MESSAGE_TYPE ||
      !Number.isFinite(PRICE_YEN)
    ) {
      setErr("必須項目が不足しています。入力内容を確認してください。");
      throw new Error("MISSING_FIELDS");
    }

    // ① PaymentIntentを確定（カードを紐づける）
    await elements.submit(); 

    const { error: payError, paymentIntent } = await stripe.confirmPayment({
      elements,
      clientSecret: CLIENT_SECRET,
      redirect: "if_required",
    });

    if (payError) {
      setErr(payError.message || "決済に失敗しました。");
      throw new Error("STRIPE_PAYMENT_ERROR: " + (payError.message || ""));
    }

    // オーソリ済みかチェック
    if (
      !paymentIntent ||
      !["requires_capture", "processing", "succeeded"].includes(
        paymentIntent.status
      )
    ) {
      setErr("決済の状態を確認できませんでした。");
      throw new Error("INVALID_PAYMENT_INTENT_STATUS");
    }

    // ② オーソリ済みPIを使ってordersを作る
    const payload = {
      payment_intent_id: PAYMENT_INTENT_ID,
      seller_creator_id: SELLER_CREATOR_ID,
      request_user_id: REQUEST_USER_ID,
      message_type: MESSAGE_TYPE,
      price: PRICE_YEN,
      genre: GENRE,
      to_type: TO_TYPE,
      to_name: TO_NAME,
      message: MSG,
      memo: MEMO,
    };

    let res;
    try {
      res = await fetch(SAVE_ENDPOINT, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${JWT}`,
        },
        body: JSON.stringify(payload),
      });
    } catch {
      setErr("注文保存APIの呼び出しに失敗しました。");
      throw new Error("NETWORK_ERROR");
    }

    const data = await res.json().catch(() => ({}));

    if (!res.ok) {
      const msg = (data.message || "").toString();

      if (
        msg.toLowerCase().includes("invalid jwt") ||
        msg.toLowerCase().includes("jwt expired") ||
        res.status === 401
      ) {
        setErr("ログインの有効期限が切れています。再度ログインしてください。");
        throw new Error("JWT_EXPIRED");
      }
      if (res.status === 409 || (data.code || "").toString() === "OUT_OF_STOCK") {
        setErr(
          data.message ||
            "在庫がありません。時間をおいて再度お試しください。"
        );
        throw new Error("OUT_OF_STOCK");
      }

      setErr(msg || `注文保存APIエラー: ${res.status}`);
      throw new Error("SAVE_ORDER_ERROR: " + (msg || res.status));
    }

    // 成功時
    const order_id = data.order_id || "";
    const order_number = data.order_number || "";

    setErr("");
    variables["last_payment_saved"] = true;
    variables["last_payment_error"] = "";
    variables["current_order_id"] = order_id;
    variables["current_order_number"] = order_number;

    document
      .getElementById("save-payment-button-active")
      ?.classList.add("hidden");

    return { order_id, order_number };
  } catch (e) {
    variables["last_payment_saved"] = false;
    variables["last_payment_error"] = e?.message || "unknown_error";
    throw e;
  }
})();

