// WeWeb 変数ID
const VAR_ID = 'e1c8df21-4353-4fd7-9c54-96263f1ac0e0';

// 進行中なら何もしない（多重リセット防止）
const isRunning = !!window._countdownInterval;
const current = Number(variables[VAR_ID]);

if (isRunning && Number.isFinite(current) && current > 0) {
  // 進行中なのでスキップ
  return { ok: true, skipped: true, remaining: current };
}

// ここから新規開始（未起動 or 終了済み）
let sec = 60;
variables[VAR_ID] = sec;

// 既存 interval があれば片付けてから開始（終了直後などの保険）
if (window._countdownInterval) {
  clearInterval(window._countdownInterval);
  delete window._countdownInterval;
}

window._countdownInterval = setInterval(() => {
  sec--;

  if (sec < 0) {
    clearInterval(window._countdownInterval);
    delete window._countdownInterval; // => 次回の開始を許可
    sec = 0;
  }

  variables[VAR_ID] = sec;
}, 1000);

return { ok: true, started: true, remaining: sec };

