return (function (ts) {
    // 入力をミリ秒に正規化（Date / ISO / 数値[秒orミリ秒]）
    function toMs(v) {
        if (v == null || v === '') return NaN;
        if (v instanceof Date) return v.getTime();
        if (typeof v === 'number') return v < 1e12 ? v * 1000 : v;        // 秒なら×1000
        if (typeof v === 'string' && /^\d+$/.test(v)) {
            var n = Number(v); return n < 1e12 ? n * 1000 : n;               // 数字文字列も対応
        }
        var t = Date.parse(v); return isNaN(t) ? NaN : t;                  // ISO等
    }

    var thenMs = toMs(ts);
    if (isNaN(thenMs)) return '';

    var nowMs = Date.now();
    var diff = nowMs - thenMs;
    if (diff < 0) diff = 0;

    var m = 60000, h = 3600000, d = 86400000, w = 604800000;

    // 〜59分未満
    if (diff < m) return 'たった今';
    // 1時間未満
    if (diff < h) return Math.max(1, Math.floor(diff / m)) + '分前';
    // 24時間未満
    if (diff < d) return Math.floor(diff / h) + '時間前';
    // 1週間未満
    if (diff < 7 * d) return Math.floor(diff / d) + '日前';
    // 1〜4週間未満（=30日未満は週で表示）
    if (diff < 30 * d) return Math.floor(diff / w) + '週間前';

    // 1ヶ月超 → 月/年で表示（カレンダー月差で計算）
    var a = new Date(thenMs), b = new Date(nowMs);
    var months = (b.getFullYear() - a.getFullYear()) * 12 + (b.getMonth() - a.getMonth());
    if (b.getDate() < a.getDate()) months -= 1;  // 当月の満了日に達していなければ1か月引く
    if (months < 1) months = 1;                  // 30日超でここに来るのでセーフティ

    if (months < 12) return months + 'ヶ月前';
    var years = Math.floor(months / 12);
    return years + '年前';
})(input);
