  return fileObj
    .arrayBuffer()
    .then(buffer => {
      const mime = fileObj.mimeType || fileObj.type || 'application/octet-stream';
      const realBlob = new Blob([buffer], { type: mime });
      return URL.createObjectURL(realBlob);
    })
    .catch(err => {
      throw new Error("arrayBuffer() の取得に失敗しました: " + err.message);
    });
}

