// 秒数チェック（60秒以内 = 61秒未満OK）

const LIMIT_SECONDS = 61; // 61未満はOK（= 60.9秒までOK）

return await (async () => {
  const fileObj =
    variables[/* video message upload  - value */ 'e8f0a1bf-38aa-4955-bb42-c78e9b591d4e-value']?.[0];

  if (!fileObj) {
    throw new Error("動画ファイルが選択されていません。もう一度お試しください。");
  }

  // video要素で秒数取得
  const duration = await new Promise((resolve, reject) => {
    const video = document.createElement("video");
    video.preload = "metadata";

    video.onloadedmetadata = () => {
      const d = video.duration;
      URL.revokeObjectURL(video.src);

      // Safari対策
      if (!Number.isFinite(d) || d <= 0) {
        reject(new Error("動画の長さを判定できませんでした。"));
        return;
      }

      resolve(d);
    };

    video.onerror = () => {
      reject(new Error("動画のメタデータを読み取れませんでした。"));
    };

    video.src = URL.createObjectURL(fileObj);
  });

  // ★ 判定：61秒以上はNG（60.9秒はOK）
  if (duration >= LIMIT_SECONDS) {
    const displaySeconds = Math.floor(duration);

    throw new Error(
      `動画が長すぎます（約 ${displaySeconds} 秒）。60秒以内にしてください。`
    );
  }

  // 表示・保存用は整数
  const durationSeconds = Math.floor(duration);

  return {
    ok: true,
    name: fileObj.name || null,
    type: fileObj.type || fileObj.mimeType || null,
    durationSeconds,
  };
})();

