        if (video.paused) {
          video.play().then(hidePlayBtn).catch(console.warn);
        } else {
          video.pause();
          showPlayBtn();
        }
      });

      // ミュートボタン
      muteBtn.addEventListener('click', (e) => {
        e.stopPropagation();
        video.muted = !video.muted;
