        } else if (video.webkitEnterFullscreen) {
          video.webkitEnterFullscreen();
        }
      });

      // duration反映
      const setDuration = () => {
        if (isFinite(video.duration)) {
          durationEl.textContent = fmt(video.duration);
        }
      };
      video.addEventListener('loadedmetadata', setDuration);
      video.addEventListener('durationchange', setDuration);

      // 再生中にシーク・時間を更新
      video.addEventListener('timeupdate', () => {
        const cur = video.currentTime || 0;
        const dur = video.duration || 0;
        currentTimeEl.textContent = fmt(cur);
        if (dur > 0) {
          seek.value = ((cur / dur) * 100).toString();
        }
      });

      // シークで移動
      seek.addEventListener('input', (e) => {
        const val = Number(e.target.value);
        const dur = video.duration || 0;
        if (dur > 0) {
          const newTime = (val / 100) * dur;
          currentTimeEl.textContent = fmt(newTime);
        }
      });
      seek.addEventListener('change', (e) => {
        const val = Number(e.target.value);
        const dur = video.duration || 0;
        if (dur > 0) {
          const newTime = (val / 100) * dur;
          video.currentTime = newTime;
        }
      });
    }, 200);
  }

